SELECT s.B#, s.first_name, s.last_name, s.gpa FROM students s JOIN (SELECT a.g_B# FROM (SELECT g.g_B# FROM g_enrollments g JOIN classes c ON g.classid = c.classid WHERE c.dept_code = 'CS' GROUP BY g.g_B#) a JOIN (SELECT g.g_B# FROM g_enrollments g JOIN classes c ON g.classid = c.classid WHERE c.dept_code = 'Math' GROUP BY g.g_B#) b ON a.g_B# = b.g_B#) g_enroll ON s.B# = g_enroll.g_B#;